import {createContext, useReducer} from "react"
import AuthReducer from "./authReducer";

const initialState = {
    user: {
        _id: "64021589311763cb226a94cc", username: "Jane", "email": "jane@gmail.com", "password": "$2b$10$2THiukhCmEUF7kND9hBHieXxLrs2AmdMWENbpvq4OlLncrRO9BhyS", "profilePicture": "", "coverPicture": "", "followers": [], "following": [], "isAdmin": false, "createdAt": { "$date": { "$numberLong": "1677858185486" } }, "updatedAt": { "$date": { "$numberLong": "1677884819541" } }, "__v": { "$numberInt": "0" }, "city": "Atlanta", "desc": "fun loving gal", "from": "New York"
    },
    isFetching: false,
    error: false
}

export const AuthContext = createContext(initialState);

export const AuthContextProvider = ({children}) => {
    const [state, dispatch] = useReducer(AuthReducer, initialState);

    return (
        <AuthContext.Provider value={{
            user: state.user,
            isFetching: state.isFetching,
            error: state.error,
            dispatch
        }}>
            {children}
        </AuthContext.Provider>
    )
}
