import React, { useContext, useEffect } from "react";
import { useState } from "react";
import ChatOnline from "../../components/chatOnline/chatOnline";
import Conversation from "../../components/conversation/conversation";
import Message from "../../components/message/message";
import TopBar from "../../components/topbar/topbar";
import "./messenger.css";
import { AuthContext } from "../../context/authContext";
import axios from "axios";

export default function Messenger() {
  const [conversations, setConversations] = useState([]);

  const { user } = useContext(AuthContext);
  useEffect(() => {
    const getConversations = async () => {
      try {
        const res = await axios.get(`/conversations/${user._id}`);
        setConversations(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    getConversations();
  }, [user]);

  return (
    <>
      <TopBar />
      <div className="messenger">
        <div className="chatMenu">
          <div className="chatMenuContainer">
            <input
              type="search"
              placeholder="Search For Friends"
              className="chatMenuSearch"
            />
            {conversations.map((c) => (
              <Conversation conversation={c} user={user} />
            ))}
          </div>
        </div>
        <div className="chatBox">
          <div className="chatBoxContainer">
            <div className="chatBoxTop">
              <Message />
              <Message own={true} />
              <Message />
              <Message />
              <Message />
              <Message />
              <Message />
              <Message />
              <Message />
              <Message />
              <Message />
              <Message />
              <Message />
              <Message />
              <Message />
            </div>
            <div className="chatBoxBottom">
              <textarea
                placeholder="Enter message here..."
                className="chatBoxBottomMessage"
              ></textarea>
              <button className="chatBoxBottomButton">Send</button>
            </div>
          </div>
        </div>
        <div className="chatOnline">
          <div className="chatOnineContainer">
            <ChatOnline />
            <ChatOnline />
            <ChatOnline />
          </div>
        </div>
      </div>
    </>
  );
}
