import { useRef } from "react";
import "./register.css";

const Register = () => {

  const email = useRef();
  const username = useRef();
  const password = useRef();
  const passwordAgain = useRef();

  const handleClick = (e) => {
    e.preventDefault();
    if (passwordAgain.current.value !== password.current.value) {
      passwordAgain.current.setCustomValidity("Passwords don't match!!")
    }
    console.log(password.current);
  }

  return (
    <div className="login">
      <div className="loginWrapper">
        <div className="loginLeft">
          <h3 className="loginLogo">AntiSociaL</h3>
          <span className="loginDesc">Meet people you know and don't know</span>
        </div>
        <div className="loginRight" onSubmit={handleClick}>
          <form className="registerBox">
            <input
              type="text"
              className="loginEmail loginInput"
              placeholder="Username"
              ref={username}
              required
            />
            <input
              type="email"
              className="loginEmail loginInput"
              placeholder="Email"
              ref={email}
              required
            />
            <input
              type="password"
              className="loginEmail loginInput"
              placeholder="Password"
              ref={password}
              required
              minLength={8}
            />
            <input
              type="password"
              className="loginEmail loginInput"
              placeholder="Re-type Password"
              ref={passwordAgain}
              required
            />
            <button className="loginButton" type="submit">Sign Up</button>
            <hr />
            <p>
              <span className="registerText">Already have an account ?</span>
            </p>
            <button className="loginRegisterButton">Log in to Account</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
